import { defineComponent } from "vue";
const componentName = "IButtonGroup";
export default defineComponent({
  name: componentName,
  inject: {
    form: {
      default: () => ({})
    },
    buttonGroup: {
      default: () => ({})
    },
    formGroup: {
      default: () => ({})
    }
  },
  provide() {
    return {
      buttonGroup: this
    };
  },
  props: {
    vertical: {
      type: Boolean,
      default: false
    },
    block: {
      type: Boolean,
      default: false
    },
    disabled: {
      type: Boolean,
      default: false
    }
  },
  computed: {
    classes() {
      return {
        "-vertical": this.vertical,
        "-block": this.block,
        "-disabled": this.isDisabled
      };
    },
    isDisabled() {
      return this.disabled || this.buttonGroup.disabled || this.form.disabled || this.formGroup.disabled;
    }
  }
});
