import { defineComponent } from "vue";
import {
  LinkableMixin
} from "@inkline/inkline/mixins";
const componentName = "IListGroupItem";
export default defineComponent({
  name: componentName,
  mixins: [
    LinkableMixin
  ],
  props: {
    active: {
      type: Boolean,
      default: false
    },
    disabled: {
      type: Boolean,
      default: false
    },
    tag: {
      type: String,
      default: "div"
    },
    tabindex: {
      type: [Number, String],
      default: 0
    }
  },
  computed: {
    ariaDisabled() {
      if (this.role === "link") {
        return null;
      }
      return this.disabled ? "true" : "false";
    },
    classes() {
      return {
        "-active": this.active,
        "-disabled": this.disabled
      };
    },
    role() {
      return this.$attrs.to || this.$attrs.href ? "link" : "listitem";
    },
    tabIndex() {
      return this.disabled ? -1 : this.tabindex;
    }
  }
});
