import { defineComponent } from "vue";
import { uid } from "@inkline/inkline/helpers";
const componentName = "ITab";
export default defineComponent({
  name: componentName,
  inject: {
    tabs: {
      default: () => ({})
    }
  },
  props: {
    title: {
      type: String,
      default: ""
    },
    name: {
      type: String,
      default() {
        return uid("tab");
      }
    }
  },
  computed: {
    active() {
      return this.tabs.active === this.name;
    },
    classes() {
      return {
        "-active": this.active
      };
    }
  }
});
